/* Test array operations */
program
    type intArray = array [1 .. 5] of integer;
    var numbers : intArray;
        i, sum : integer;
begin
    /* Initialize array */
    i := 1;
    while i <= 5 do
    begin
        numbers[i] := i * 2;
        i := i + 1
    end;

    /* Calculate sum */
    sum := 0;
    i := 1;
    while i <= 5 do
    begin
        sum := sum + numbers[i];
        i := i + 1
    end;

    write(sum);  /* Should be 30 */
    write(numbers[3])  /* Should be 6 */
end.

test8.pl.source:
/* Test function calls */
program
    var x, y, result : integer;

    function add(a : integer; b : integer) : integer;
    begin
        add := a + b
    end;

    function multiply(a : integer; b : integer) : integer;
    begin
        multiply := a * b
    end;

    procedure swap(var a : integer; var b : integer);
    var temp : integer;
    begin
        temp := a;
        a := b;
        b := temp
    end;

begin
    x := 5;
    y := 3;

    result := add(x, y);
    write(result);  /* Should be 8 */

    result := multiply(x, y);
    write(result);  /* Should be 15 */

    call swap(x, y);
    write(x);      /* Should be 3 */
    write(y)       /* Should be 5 */
end.

